DROP VIEW "public"."v_insiders_enriched";--> statement-breakpoint
CREATE VIEW "public"."v_insiders_enriched" AS (select "insider_positions"."account_hash", "insider_positions"."detected_at", "insider_positions"."total_volume", "insider_positions"."token_id", "insider_positions"."avg_price", "market_tokens"."outcome", 1 as "market_count", "token_market_lookup"."condition_id", "markets"."question", "markets"."slug", "markets"."outcomeTags", "token_stats"."last_price", "token_stats"."total_vol", "market_tokens"."winner", "markets"."closed" from "insider_positions" left join "token_market_lookup" on "insider_positions"."token_id" = "token_market_lookup"."token_id" left join "markets" on "token_market_lookup"."condition_id" = "markets"."conditionId" left join "market_tokens" on "insider_positions"."token_id" = "market_tokens"."token_id" left join "token_stats" on "insider_positions"."token_id" = "token_stats"."token");